package mcqjava;

public class Test13 {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a = 1;
		int b = 2;
		int c;
		int d;
		
		c = ++b; //c = 3 b =3
		d = a++; //d = 1 a = 2
		
		c++; //c = 4
		b++; //b = 4
		++a; //a = 3
		
		System.out.println(a+" "+b+" "+c +" "+d);
		
		

	}

}
